select.if:
select et_empl_basic.* from et_empl_basic left join synchronization_record on et_empl_basic.zhrempl = synchronization_record.bussiness_id
where synchronization_record.type = #{type} and synchronization_record.sync_status = #{syncSuccessFlag}
order by et_empl_basic.zhrempl desc
limit #{offset}, #{size}

mapper.select:
SELECT eb.zhrempl AS zhrempl, eb.zemplnm AS zemplnm, eb.zxb AS zxb, eb.gbdat AS gbdat, eb.zphoneno AS zphoneno, eb.ztelno AS ztelno, eb.zzjgj AS zzjgj, eb.zzjlx AS zzjlx, eb.zzjhm AS zzjhm, eb.zemail AS zemail, eb.zstas_e AS zstasE, ep.zhrpost AS zhrpost, ep.zzx AS zzx, ep.zstat_ps AS zstatPs, pb.zpostnam AS zpostnam, pb.zps_org AS zpsOrg, ob.zorgnm AS zorgnm, sr.sync_status as syncFlag FROM et_empl_basic eb LEFT JOIN et_empl_post ep ON eb.ZHREMPL = ep.ZHREMPL LEFT JOIN it_post_basic pb ON ep.ZHRPOST = pb.zhrpost LEFT JOIN it_org_basic ob ON pb.ZPS_ORG = ob.ZHRORG left join synchronization_record sr on (sr.bussiness_id = eb.ZHREMPL and sr.extend_2 = pb.zhrpost and pb.ZPS_ORG = sr.extend_3)
and sr.sync_status = #{query.syncFlag}
and sr.sync_status is null
order by eb.zhrempl desc limit #{query.page}, #{query.limit}

SELECT count(1) FROM et_empl_basic eb LEFT JOIN et_empl_post ep ON eb.ZHREMPL = ep.ZHREMPL LEFT JOIN it_post_basic pb ON ep.ZHRPOST = pb.zhrpost LEFT JOIN it_org_basic ob ON pb.ZPS_ORG = ob.ZHRORG left join synchronization_record sr on (sr.bussiness_id = eb.ZHREMPL and sr.extend_2 = pb.zhrpost and pb.ZPS_ORG = sr.extend_3)
and sr.sync_status = #{query.syncFlag}
and sr.sync_status is null
